import sysimport RPi.GPIO as GPIOfrom time import sleepsignal = 'rest'def servoControl(pin, degree, t = 1):    GPIO.setmode(GPIO.BCM)    GPIO.setup(pin, GPIO.OUT)    pwm = GPIO.PWM(pin, 50)    pwm.start(5)    pwm.ChangeDutyCycle(degree)    sleep(t)    pwm.stop()    GPIO.cleanup(pin)try:    signal = sys.argv[1]    pin_num = int(sys.argv[2])    degree = float(sys.argv[3])    rest_degree = float(sys.argv[4])except:    print('argument error')    sys.stdout.flush()    exit()print(signal)sys.stdout.flush()servoControl(pin_num, degree, 0.6)#active servo off stateservoControl(pin_num, rest_degree, 0.2)print('rest')sys.stdout.flush()#servo rest position